    private void subActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_subActionPerformed
        // if the user wants to subtract leg A with B
        // create variables from textbox
        double a = Double.parseDouble(legA.getText());
        double b = Double.parseDouble(legB.getText());

        // mathamatical calcualtions
        double c = a - b;

        // input final answer
        legC.setText(Double.toString(c));
    }//GEN-LAST:event_subActionPerformed

    private void multiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_multiActionPerformed
        // if the user wants to multiply leg A and B
        // create variables from textbox
        double a = Double.parseDouble(legA.getText());
        double b = Double.parseDouble(legB.getText());

        // mathamatical calcualtions
        double c = a * b;

        // input final answer
        legC.setText(Double.toString(c));
    }//GEN-LAST:event_multiActionPerformed

    private void divActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_divActionPerformed
        // if the user wants to divide leg A and B
        // create variables from textbox
        double a = Double.parseDouble(legA.getText());
        double b = Double.parseDouble(legB.getText());

        // mathamatical calcualtions
        double c = a / b;

        // input final answer
        legC.setText(Double.toString(c));
    }//GEN-LAST:event_divActionPerformed

