    private void addActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addActionPerformed
        // if the user wants to add leg A and B
        // create variables from textbox
        double a = Double.parseDouble(legA.getText());
        double b = Double.parseDouble(legB.getText());

        // mathamatical calcualtions
        double c = a + b;

        // input final answer
        legC.setText(Double.toString(c));

    }//GEN-LAST:event_addActionPerformed

